import Layout from '../components/Layout';
import Fetch from 'isomorphic-unfetch'
const Index = (props)=>(
    <Layout> <br/>
    <div>
        <h1>My News App</h1>
        {props.articles.articles[0].source.name}
    </div></Layout>
)
Index.getInitialProps = async function(){
    const res = await fetch(
        'http://newsapi.org/v2/top-headlines?country=us&category=business&apiKey=d270d55ff3a548b7a6d654fa84a47124&format=json')
    const data = await res.json();
    return{
        articles : data
    }
}
export default Index;
